INSERT INTO `movies` (`movieid`, `titre`, `lecteur_link`, `description`, `categories`, `poster`, `sortie`, `director`, `cast`, `time`, `ratings`) VALUES
	(11, 'Star Wars', 'https://vidsrc.to/embed/movie/11', 'Princess Leia is captured and held hostage by the evil Imperial forces in their effort to take over the galactic Empire. Venturesome Luke Skywalker and dashing captain Han Solo team together with the loveable robot duo R2-D2 and C-3PO to rescue the beautiful princess and restore peace and justice in the Empire.', 'Adventure, Action, Science Fiction', 'https://image.tmdb.org/t/p/w400/6FfCtAuVAW8XJjZ7eWeLibRLWTw.jpg', '1977-05-25', 'George Lucas', 'Mark Hamill, Harrison Ford, Carrie Fisher, Peter Cushing, Alec Guinness', 121, 8.2),
	(12, 'Finding Nemo', 'https://vidsrc.to/embed/movie/12', 'Nemo, an adventurous young clownfish, is unexpectedly taken from his Great Barrier Reef home to a dentist\'s office aquarium. It\'s up to his worrisome father Marlin and a friendly but forgetful fish Dory to bring Nemo home -- meeting vegetarian sharks, surfer dude turtles, hypnotic jellyfish, hungry seagulls, and more along the way.', 'Animation, Family', 'https://image.tmdb.org/t/p/w400/eHuGQ10FUzK1mdOY69wF5pGgEf5.jpg', '2003-05-30', 'Andrew Stanton', 'Albert Brooks, Ellen DeGeneres, Alexander Gould, Willem Dafoe, Geoffrey Rush', 100, 7.8),
	(13, 'Forrest Gump', 'https://vidsrc.to/embed/movie/13', 'A man with a low IQ has accomplished great things in his life and been present during significant historic events—in each case, far exceeding what anyone imagined he could do. But despite all he has achieved, his one true love eludes him.', 'Comedy, Drama, Romance', 'https://image.tmdb.org/t/p/w400/arw2vcBveWOVZr6pxd9XTd1TdQa.jpg', '1994-06-23', 'Robert Zemeckis', 'Tom Hanks, Robin Wright, Gary Sinise, Sally Field, Mykelti Williamson', 142, 8.5),
	(14, 'American Beauty', 'https://vidsrc.to/embed/movie/14', 'Lester Burnham, a depressed suburban father in a mid-life crisis, decides to turn his hectic life around after developing an infatuation with his daughter\'s attractive friend.', 'Drama', 'https://image.tmdb.org/t/p/w400/wby9315QzVKdW9BonAefg8jGTTb.jpg', '1999-09-15', 'Sam Mendes', 'Kevin Spacey, Annette Bening, Thora Birch, Wes Bentley, Mena Suvari', 122, 8.0),
	(15, 'Citizen Kane', 'https://vidsrc.to/embed/movie/15', 'Newspaper magnate, Charles Foster Kane is taken from his mother as a boy and made the ward of a rich industrialist. As a result, every well-meaning, tyrannical or self-destructive move he makes for the rest of his life appears in some way to be a reaction to that deeply wounding event.', 'Mystery, Drama', 'https://image.tmdb.org/t/p/w400/sav0jxhqiH0bPr2vZFU0Kjt2nZL.jpg', '1941-04-17', 'Orson Welles', 'Orson Welles, Joseph Cotten, Dorothy Comingore, Ray Collins, George Coulouris', 119, 8.0),
	(19, 'Metropolis', 'https://vidsrc.to/embed/movie/19', 'In a futuristic city sharply divided between the rich and the poor, the son of the city\'s mastermind meets a prophet who predicts the coming of a savior to mediate their differences.', 'Drama, Science Fiction', 'https://image.tmdb.org/t/p/w400/pxbGZewX327IbTvrCVRJgcLJTSQ.jpg', '1927-02-06', 'Fritz Lang', 'Gustav Fröhlich, Brigitte Helm, Alfred Abel, Rudolf Klein-Rogge, Theodor Loos', 149, 8.2),
	(22, 'Pirates of the Caribbean: The Curse of the Black Pearl', 'https://vidsrc.to/embed/movie/22', 'Jack Sparrow, a freewheeling 18th-century pirate, quarrels with a rival pirate bent on pillaging Port Royal. When the governor\'s daughter is kidnapped, Sparrow decides to help the girl\'s love save her.', 'Adventure, Fantasy, Action', 'https://image.tmdb.org/t/p/w400/z8onk7LV9Mmw6zKz4hT6pzzvmvl.jpg', '2003-07-09', 'Gore Verbinski', 'Johnny Depp, Orlando Bloom, Keira Knightley, Geoffrey Rush, Jack Davenport', 143, 7.8),
	(24, 'Kill Bill: Vol. 1', 'https://vidsrc.to/embed/movie/24', 'An assassin is shot by her ruthless employer, Bill, and other members of their assassination circle – but she lives to plot her vengeance.', 'Action, Crime', 'https://image.tmdb.org/t/p/w400/1aRbVU0JnuEvMYDNBFEpENiSDtg.jpg', '2003-10-10', 'Quentin Tarantino', 'Uma Thurman, Lucy Liu, Vivica A. Fox, Daryl Hannah, David Carradine', 111, 8.0),
	(28, 'Apocalypse Now', 'https://vidsrc.to/embed/movie/28', 'At the height of the Vietnam war, Captain Benjamin Willard is sent on a dangerous mission that, officially, "does not exist, nor will it ever exist." His goal is to locate - and eliminate - a mysterious Green Beret Colonel named Walter Kurtz, who has been leading his personal army on illegal guerrilla missions into enemy territory.', 'Drama, War', 'https://image.tmdb.org/t/p/w400/gQB8Y5RCMkv2zwzFHbUJX3kAhvA.jpg', '1979-08-15', 'Francis Ford Coppola', 'Martin Sheen, Frederic Forrest, Albert Hall, Laurence Fishburne, Sam Bottoms', 147, 8.3),
	(38, 'Eternal Sunshine of the Spotless Mind', 'https://vidsrc.to/embed/movie/38', 'Joel Barish, heartbroken that his girlfriend underwent a procedure to erase him from her memory, decides to do the same. However, as he watches his memories of her fade away, he realises that he still loves her, and may be too late to correct his mistake.', 'Science Fiction, Drama, Romance', 'https://image.tmdb.org/t/p/w400/5MwkWH9tYHv3mV9OdYTMR5qreIz.jpg', '2004-03-19', 'Michel Gondry', 'Jim Carrey, Kate Winslet, Kirsten Dunst, Mark Ruffalo, Elijah Wood', 108, 8.1),
	(55, 'Amores Perros', 'https://vidsrc.to/embed/movie/55', 'A fatalistic car crash in Mexico city sets off a chain of events in the lives of three people: a supermodel, a young man wanting to run off with his sister-in-law, and a homeless man. Their lives are catapulted into unforeseen situations instigated by the seemingly inconsequential destiny of a dog.', 'Drama, Thriller', 'https://image.tmdb.org/t/p/w400/vV4vlD4ool5JSsS1rB82qjCF6z8.jpg', '2000-06-16', 'Alejandro González Iñárritu', 'Gael García Bernal, Vanessa Bauche, Goya Toledo, Álvaro Guerrero, Emilio Echevarría', 154, 7.6),
	(62, '2001: A Space Odyssey', 'https://vidsrc.to/embed/movie/62', 'Humanity finds a mysterious object buried beneath the lunar surface and sets off to find its origins with the help of HAL 9000, the world\'s most advanced super computer.', 'Science Fiction, Mystery, Adventure', 'https://image.tmdb.org/t/p/w400/ve72VxNqjGM69Uky4WTo2bK6rfq.jpg', '1968-04-02', 'Stanley Kubrick', 'Keir Dullea, Gary Lockwood, William Sylvester, Douglas Rain, Daniel Richter', 149, 8.1),
	(70, 'Million Dollar Baby', 'https://vidsrc.to/embed/movie/70', 'Despondent over a painful estrangement from his daughter, trainer Frankie Dunn isn\'t prepared for boxer Maggie Fitzgerald to enter his life. But Maggie\'s determined to go pro and to convince Dunn and his cohort to help her.', 'Drama', 'https://image.tmdb.org/t/p/w400/2ti3l1OeB8G94yyqfAI7nmMZUbA.jpg', '2004-12-15', 'Clint Eastwood', 'Clint Eastwood, Hilary Swank, Morgan Freeman, Jay Baruchel, Mike Colter', 132, 8.0),
	(73, 'American History X', 'https://vidsrc.to/embed/movie/73', 'Derek Vineyard is paroled after serving 3 years in prison for killing two African-American men. Through his brother, Danny Vineyard\'s narration, we learn that before going to prison, Derek was a skinhead and the leader of a violent white supremacist gang that committed acts of racial crime throughout L.A. and his actions greatly influenced Danny. Reformed and fresh out of prison, Derek severs contact with the gang and becomes determined to keep Danny from going down the same violent path as he did.', 'Drama', 'https://image.tmdb.org/t/p/w400/euypWkaYFOLW3e5rLIcTAjWnhhT.jpg', '1998-07-01', 'Tony Kaye', 'Edward Norton, Edward Furlong, Beverly D\'Angelo, Jennifer Lien, Ethan Suplee', 119, 8.4),
	(76, 'Before Sunrise', 'https://vidsrc.to/embed/movie/76', 'A young man and woman meet on a train in Europe, and wind up spending one evening together in Vienna. Unfortunately, both know that this will probably be their only night together.', 'Drama, Romance', 'https://image.tmdb.org/t/p/w400/kf1Jb1c2JAOqjuzA3H4oDM263uB.jpg', '1995-01-27', 'Richard Linklater', 'Ethan Hawke, Julie Delpy, Andrea Eckert, Haymon Maria Buttinger, Erni Mangold', 101, 8.0),
	(77, 'Memento', 'https://vidsrc.to/embed/movie/77', 'Leonard Shelby is tracking down the man who raped and murdered his wife. The difficulty of locating his wife\'s killer, however, is compounded by the fact that he suffers from a rare, untreatable form of short-term memory loss. Although he can recall details of life before his accident, Leonard cannot remember what happened fifteen minutes ago, where he\'s going, or why.', 'Mystery, Thriller', 'https://image.tmdb.org/t/p/w400/yuNs09hvpHVU1cBTCAk9zxsL2oW.jpg', '2000-10-11', 'Christopher Nolan', 'Guy Pearce, Carrie-Anne Moss, Joe Pantoliano, Mark Boone Junior, Russ Fega', 113, 8.2),
	(78, 'Blade Runner', 'https://vidsrc.to/embed/movie/78', 'In the smog-choked dystopian Los Angeles of 2019, blade runner Rick Deckard is called out of retirement to terminate a quartet of replicants who have escaped to Earth seeking their creator for a way to extend their short life spans.', 'Science Fiction, Drama, Thriller', 'https://image.tmdb.org/t/p/w400/63N9uy8nd9j7Eog2axPQ8lbr3Wj.jpg', '1982-06-25', 'Ridley Scott', 'Harrison Ford, Rutger Hauer, Sean Young, Edward James Olmos, M. Emmet Walsh', 118, 7.9),
	(80, 'Before Sunset', 'https://vidsrc.to/embed/movie/80', 'Nine years later, Jesse travels across Europe giving readings from a book he wrote about the night he spent in Vienna with Celine. After his reading in Paris, Celine finds him, and they spend part of the day together before Jesse has to again leave for a flight. They are both in relationships now, and Jesse has a son, but as their strong feelings for each other start to return, both confess a longing for more.', 'Drama, Romance', 'https://image.tmdb.org/t/p/w400/gycdE1ARByGQcK4fYR2mgpU6OO.jpg', '2004-06-16', 'Richard Linklater', 'Ethan Hawke, Julie Delpy, Vernon Dobtcheff, Louise Lemoine Torrès, Rodolphe Pauly', 80, 7.8),
	(85, 'Raiders of the Lost Ark', 'https://vidsrc.to/embed/movie/85', 'When Dr. Indiana Jones – the tweed-suited professor who just happens to be a celebrated archaeologist – is hired by the government to locate the legendary Ark of the Covenant, he finds himself up against the entire Nazi regime.', 'Adventure, Action', 'https://image.tmdb.org/t/p/w400/ceG9VzoRAVGwivFU403Wc3AHRys.jpg', '1981-06-12', 'Steven Spielberg', 'Harrison Ford, Karen Allen, Paul Freeman, John Rhys-Davies, Ronald Lacey', 115, 7.9),
	(89, 'Indiana Jones and the Last Crusade', 'https://vidsrc.to/embed/movie/89', 'In 1938, an art collector appeals to eminent archaeologist Dr. Indiana Jones to embark on a search for the Holy Grail. Indy learns that a medieval historian has vanished while searching for it, and the missing man is his own father, Dr. Henry Jones Sr.. He sets out to rescue his father by following clues in the old man\'s notebook, which his father had mailed to him before he went missing. Indy arrives in Venice, where he enlists the help of a beautiful academic, Dr. Elsa Schneider, along with Marcus Brody and Sallah. Together they must stop the Nazis from recovering the power of eternal life and taking over the world!', 'Adventure, Action', 'https://image.tmdb.org/t/p/w400/npUCnxl1fqgt3aQiMQq0iJdZMZp.jpg', '1989-05-24', 'Steven Spielberg', 'Harrison Ford, Sean Connery, Denholm Elliott, Alison Doody, John Rhys-Davies', 127, 7.9),
	(98, 'Gladiator', 'https://vidsrc.to/embed/movie/98', 'In the year 180, the death of emperor Marcus Aurelius throws the Roman Empire into chaos.  Maximus is one of the Roman army\'s most capable and trusted generals and a key advisor to the emperor.  As Marcus\' devious son Commodus ascends to the throne, Maximus is set to be executed.  He escapes, but is captured by slave traders.  Renamed Spaniard and forced to become a gladiator, Maximus must battle to the death with other men for the amusement of paying audiences.', 'Action, Drama, Adventure', 'https://image.tmdb.org/t/p/w400/ty8TGRuvJLPUmAR1H1nRIsgwvim.jpg', '2000-05-04', 'Ridley Scott', 'Russell Crowe, Joaquin Phoenix, Connie Nielsen, Oliver Reed, Richard Harris', 155, 8.2),
	(100, 'Lock, Stock and Two Smoking Barrels', 'https://vidsrc.to/embed/movie/100', 'A card shark and his unwillingly-enlisted friends need to make a lot of cash quick after losing a sketchy poker match. To do this they decide to pull a heist on a small-time gang who happen to be operating out of the flat next door.', 'Comedy, Crime', 'https://image.tmdb.org/t/p/w400/wt2TRBmFmBn5M5MBcPTwovlREaB.jpg', '1998-08-28', 'Guy Ritchie', 'Jason Flemyng, Dexter Fletcher, Nick Moran, Jason Statham, Steven Mackintosh', 105, 8.1),
	(101, 'Léon: The Professional', 'https://vidsrc.to/embed/movie/101', 'Léon, the top hit man in New York, has earned a rep as an effective "cleaner". But when his next-door neighbors are wiped out by a loose-cannon DEA agent, he becomes the unwilling custodian of 12-year-old Mathilda. Before long, Mathilda\'s thoughts turn to revenge, and she considers following in Léon\'s footsteps.', 'Crime, Drama, Action', 'https://image.tmdb.org/t/p/w400/yI6X2cCM5YPJtxMhUd3dPGqDAhw.jpg', '1994-09-14', 'Luc Besson', 'Jean Reno, Natalie Portman, Gary Oldman, Danny Aiello, Peter Appel', 111, 8.3),
	(103, 'Taxi Driver', 'https://vidsrc.to/embed/movie/103', 'A mentally unstable Vietnam War veteran works as a night-time taxi driver in New York City where the perceived decadence and sleaze feed his urge for violent action.', 'Crime, Drama', 'https://image.tmdb.org/t/p/w400/ekstpH614fwDX8DUln1a2Opz0N8.jpg', '1976-02-09', 'Martin Scorsese', 'Robert De Niro, Jodie Foster, Cybill Shepherd, Harvey Keitel, Peter Boyle', 114, 8.2),
	(107, 'Snatch', 'https://vidsrc.to/embed/movie/107', 'Unscrupulous boxing promoters, violent bookmakers, a Russian gangster, incompetent amateur robbers and supposedly Jewish jewelers fight to track down a priceless stolen diamond.', 'Crime, Comedy', 'https://image.tmdb.org/t/p/w400/56mOJth6DJ6JhgoE2jtpilVqJO.jpg', '2000-09-01', 'Guy Ritchie', 'Jason Statham, Brad Pitt, Stephen Graham, Dennis Farina, Alan Ford', 103, 7.8),
	(111, 'Scarface', 'https://vidsrc.to/embed/movie/111', 'After getting a green card in exchange for assassinating a Cuban government official, Tony Montana stakes a claim on the drug trade in Miami. Viciously murdering anyone who stands in his way, Tony eventually becomes the biggest drug lord in the state, controlling nearly all the cocaine that comes through Miami. But increased pressure from the police, wars with Colombian drug cartels and his own drug-fueled paranoia serve to fuel the flames of his eventual downfall.', 'Action, Crime, Drama', 'https://image.tmdb.org/t/p/w400/iQ5ztdjvteGeboxtmRdXEChJOHh.jpg', '1983-12-09', 'Brian De Palma', 'Al Pacino, Steven Bauer, Michelle Pfeiffer, Mary Elizabeth Mastrantonio, Robert Loggia', 170, 8.2),
	(115, 'The Big Lebowski', 'https://vidsrc.to/embed/movie/115', 'Jeffrey \'The Dude\' Lebowski, a Los Angeles slacker who only wants to bowl and drink White Russians, is mistaken for another Jeffrey Lebowski, a wheelchair-bound millionaire, and finds himself dragged into a strange series of events involving nihilists, adult film producers, ferrets, errant toes, and large sums of money.', 'Comedy, Crime', 'https://image.tmdb.org/t/p/w400/3bv6WAp6BSxxYvB5ozKFUYuRA8C.jpg', '1998-03-06', 'Joel Coen', 'Jeff Bridges, John Goodman, Steve Buscemi, Julianne Moore, David Huddleston', 117, 7.8),
	(120, 'The Lord of the Rings: The Fellowship of the Ring', 'https://vidsrc.to/embed/movie/120', 'Young hobbit Frodo Baggins, after inheriting a mysterious ring from his uncle Bilbo, must leave his home in order to keep it from falling into the hands of its evil creator. Along the way, a fellowship is formed to protect the ringbearer and make sure that the ring arrives at its final destination: Mt. Doom, the only place where it can be destroyed.', 'Adventure, Fantasy, Action', 'https://image.tmdb.org/t/p/w400/6oom5QYQ2yQTMJIbnvbkBL9cHo6.jpg', '2001-12-18', 'Peter Jackson', 'Elijah Wood, Ian McKellen, Sean Astin, Viggo Mortensen, Christopher Lee', 179, 8.4),
	(121, 'The Lord of the Rings: The Two Towers', 'https://vidsrc.to/embed/movie/121', 'Frodo and Sam are trekking to Mordor to destroy the One Ring of Power while Gimli, Legolas and Aragorn search for the orc-captured Merry and Pippin. All along, nefarious wizard Saruman awaits the Fellowship members at the Orthanc Tower in Isengard.', 'Adventure, Fantasy, Action', 'https://image.tmdb.org/t/p/w400/5VTN0pR8gcqV3EPUHHfMGnJYN9L.jpg', '2002-12-18', 'Peter Jackson', 'Elijah Wood, Sean Astin, Andy Serkis, Viggo Mortensen, Ian McKellen', 179, 8.4),
	(122, 'The Lord of the Rings: The Return of the King', 'https://vidsrc.to/embed/movie/122', 'Aragorn is revealed as the heir to the ancient kings as he, Gandalf and the other members of the broken fellowship struggle to save Gondor from Sauron\'s forces. Meanwhile, Frodo and Sam take the ring closer to the heart of Mordor, the dark lord\'s realm.', 'Adventure, Fantasy, Action', 'https://image.tmdb.org/t/p/w400/rCzpDGLbOoPwLjy3OAm5NUPOTrC.jpg', '2003-12-01', 'Peter Jackson', 'Elijah Wood, Sean Astin, Ian McKellen, Viggo Mortensen, Andy Serkis', 201, 8.5),
	(128, 'Princess Mononoke', 'https://vidsrc.to/embed/movie/128', 'Ashitaka, a prince of the disappearing Emishi people, is cursed by a demonized boar god and must journey to the west to find a cure. Along the way, he encounters San, a young human woman fighting to protect the forest, and Lady Eboshi, who is trying to destroy it. Ashitaka must find a way to bring balance to this conflict.', 'Adventure, Fantasy, Animation', 'https://image.tmdb.org/t/p/w400/cMYCDADoLKLbB83g4WnJegaZimC.jpg', '1997-07-12', 'Hayao Miyazaki', 'Youji Matsuda, Yuriko Ishida, Yuko Tanaka, Kaoru Kobayashi, Masahiko Nishimura', 134, 8.3),
	(137, 'Groundhog Day', 'https://vidsrc.to/embed/movie/137', 'A narcissistic TV weatherman, along with his attractive-but-distant producer, and his mawkish cameraman, is sent to report on Groundhog Day in the small town of Punxsutawney, where he finds himself repeating the same day over and over.', 'Romance, Fantasy, Drama, Comedy', 'https://image.tmdb.org/t/p/w400/4TszNbG17nMoS7HZaVl6OCfJdDs.jpg', '1993-02-11', 'Harold Ramis', 'Bill Murray, Andie MacDowell, Chris Elliott, Stephen Tobolowsky, Brian Doyle-Murray', 101, 7.6),
	(147, 'The 400 Blows', 'https://vidsrc.to/embed/movie/147', 'For young Parisian boy Antoine Doinel, life is one difficult situation after another. Surrounded by inconsiderate adults, including his neglectful parents, Antoine spends his days with his best friend, Rene, trying to plan for a better life. When one of their schemes goes awry, Antoine ends up in trouble with the law, leading to even more conflicts with unsympathetic authority figures.', 'Drama', 'https://image.tmdb.org/t/p/w400/12PuU23kkDLvTd0nb8hMlE3oShB.jpg', '1959-06-03', 'François Truffaut', 'Jean-Pierre Léaud, Claire Maurier, Albert Rémy, Georges Flamant, Patrick Auffay', 99, 8.1),
	(155, 'The Dark Knight', 'https://vidsrc.to/embed/movie/155', 'Batman raises the stakes in his war on crime. With the help of Lt. Jim Gordon and District Attorney Harvey Dent, Batman sets out to dismantle the remaining criminal organizations that plague the streets. The partnership proves to be effective, but they soon find themselves prey to a reign of chaos unleashed by a rising criminal mastermind known to the terrified citizens of Gotham as the Joker.', 'Drama, Action, Crime, Thriller', 'https://image.tmdb.org/t/p/w400/qJ2tW6WMUDux911r6m7haRef0WH.jpg', '2008-07-16', 'Christopher Nolan', 'Christian Bale, Heath Ledger, Michael Caine, Gary Oldman, Aaron Eckhart', 152, 8.5),
	(185, 'A Clockwork Orange', 'https://vidsrc.to/embed/movie/185', 'In a near-future Britain, young Alexander DeLarge and his pals get their kicks beating and raping anyone they please. When not destroying the lives of others, Alex swoons to the music of Beethoven. The state, eager to crack down on juvenile crime, gives an incarcerated Alex the option to undergo an invasive procedure that\'ll rob him of all personal agency. In a time when conscience is a commodity, can Alex change his tune?', 'Science Fiction, Crime', 'https://image.tmdb.org/t/p/w400/4sHeTAp65WrSSuc05nRBKddhBxO.jpg', '1971-12-19', 'Stanley Kubrick', 'Malcolm McDowell, Patrick Magee, Adrienne Corri, Miriam Karlin, Michael Bates', 137, 8.2),
	(194, 'Amélie', 'https://vidsrc.to/embed/movie/194', 'At a tiny Parisian café, the adorable yet painfully shy Amélie accidentally discovers a gift for helping others. Soon Amelie is spending her days as a matchmaker, guardian angel, and all-around do-gooder. But when she bumps into a handsome stranger, will she find the courage to become the star of her very own love story?', 'Comedy, Romance', 'https://image.tmdb.org/t/p/w400/nSxDa3M9aMvGVLoItzWTepQ5h5d.jpg', '2001-04-25', 'Jean-Pierre Jeunet', 'Audrey Tautou, Mathieu Kassovitz, Rufus, Lorella Cravotta, Serge Merlin', 122, 7.9),
	(196, 'Back to the Future Part III', 'https://vidsrc.to/embed/movie/196', 'The final installment of the Back to the Future trilogy finds Marty digging the trusty DeLorean out of a mineshaft and looking for Doc in the Wild West of 1885. But when their time machine breaks down, the travelers are stranded in a land of spurs. More problems arise when Doc falls for pretty schoolteacher Clara Clayton, and Marty tangles with Buford Tannen.', 'Adventure, Comedy, Science Fiction', 'https://image.tmdb.org/t/p/w400/crzoVQnMzIrRfHtQw0tLBirNfVg.jpg', '1990-05-25', 'Robert Zemeckis', 'Michael J. Fox, Christopher Lloyd, Thomas F. Wilson, Mary Steenburgen, Lea Thompson', 119, 7.5),
	(197, 'Braveheart', 'https://vidsrc.to/embed/movie/197', 'Enraged at the slaughter of Murron, his new bride and childhood love, Scottish warrior William Wallace slays a platoon of the local English lord\'s soldiers. This leads the village to revolt and, eventually, the entire country to rise up against English rule.', 'Action, Drama, History, War', 'https://image.tmdb.org/t/p/w400/or1gBugydmjToAEq7OZY0owwFk.jpg', '1995-05-24', 'Mel Gibson', 'Mel Gibson, Catherine McCormack, Sophie Marceau, Patrick McGoohan, Angus Macfadyen', 177, 7.9),
	(204, 'The Wages of Fear', 'https://vidsrc.to/embed/movie/204', 'In a run-down South American town, four men are paid to drive trucks loaded with nitroglycerin into the jungle through to the oil field. Friendships are tested and rivalries develop as they embark upon the perilous journey.', 'Drama, Thriller, Adventure', 'https://image.tmdb.org/t/p/w400/d1VEnsFO7YcEc5gRqKXu2Bq2MMJ.jpg', '1953-04-22', 'Henri-Georges Clouzot', 'Yves Montand, Charles Vanel, Peter van Eyck, Folco Lulli, Véra Clouzot', 148, 8.0),
	(205, 'Hotel Rwanda', 'https://vidsrc.to/embed/movie/205', 'Inspired by true events, this film takes place in Rwanda in the 1990s when more than a million Tutsis were killed in a genocide that went mostly unnoticed by the rest of the world. Hotel owner Paul Rusesabagina houses over a thousand refuges in his hotel in attempt to save their lives.', 'Drama, History, War', 'https://image.tmdb.org/t/p/w400/p3pHw85UMZPegfMZBA6dZ06yarm.jpg', '2004-12-22', 'Terry George', 'Don Cheadle, Sophie Okonedo, Nick Nolte, Fana Mokoena, Joaquin Phoenix', 121, 7.7),
	(207, 'Dead Poets Society', 'https://vidsrc.to/embed/movie/207', 'At an elite, old-fashioned boarding school in New England, a passionate English teacher inspires his students to rebel against convention and seize the potential of every day, courting the disdain of the stern headmaster.', 'Drama', 'https://image.tmdb.org/t/p/w400/azrIJJfdEL3UQhBmWhFe2b7bSCX.jpg', '1989-06-02', 'Peter Weir', 'Robin Williams, Ethan Hawke, Robert Sean Leonard, Gale Hansen, Josh Charles', 128, 8.3),
	(213, 'North by Northwest', 'https://vidsrc.to/embed/movie/213', 'Advertising man Roger Thornhill is mistaken for a spy, triggering a deadly cross-country chase.', 'Mystery, Thriller, Adventure', 'https://image.tmdb.org/t/p/w400/8gvfRlVpcKaTVqipXpYOGWBN1aO.jpg', '1959-07-08', 'Alfred Hitchcock', 'Cary Grant, Eva Marie Saint, James Mason, Jessie Royce Landis, Leo G. Carroll', 136, 8.0),
	(218, 'The Terminator', 'https://vidsrc.to/embed/movie/218', 'In the post-apocalyptic future, reigning tyrannical supercomputers teleport a cyborg assassin known as the "Terminator" back to 1984 to kill Sarah Connor, whose unborn son is destined to lead insurgents against 21st century mechanical hegemony. Meanwhile, the human-resistance movement dispatches a lone warrior to safeguard Sarah. Can he stop the virtually indestructible killing machine?', 'Action, Thriller, Science Fiction', 'https://image.tmdb.org/t/p/w400/hzXSE66v6KthZ8nPoLZmsi2G05j.jpg', '1984-10-26', 'James Cameron', 'Linda Hamilton, Michael Biehn, Arnold Schwarzenegger, Lance Henriksen, Paul Winfield', 108, 7.6),
	(223, 'Rebecca', 'https://vidsrc.to/embed/movie/223', 'Story of a young woman who marries a fascinating widower only to find out that she must live in the shadow of his former wife, Rebecca, who died mysteriously several years earlier. The young wife must come to grips with the terrible secret of her handsome, cold husband, Max De Winter. She must also deal with the jealous, obsessed Mrs. Danvers, the housekeeper, who will not accept her as the mistress of the house.', 'Mystery, Romance, Thriller, Drama', 'https://image.tmdb.org/t/p/w400/1qz3qUOHnVy7dL7M7G8jSErxE4b.jpg', '1940-03-23', 'Alfred Hitchcock', 'Laurence Olivier, Joan Fontaine, George Sanders, Judith Anderson, Nigel Bruce', 130, 7.9),
	(235, 'Stand by Me', 'https://vidsrc.to/embed/movie/235', 'Gordie, Chris, Teddy and Vern are four friends who decide to hike to find the corpse of Ray Brower, a local teenager, who was hit by a train while plucking blueberries in the wild.', 'Crime, Drama', 'https://image.tmdb.org/t/p/w400/vz0w9BSehcqjDcJOjRaCk7fgJe7.jpg', '1986-08-08', 'Rob Reiner', 'Wil Wheaton, River Phoenix, Jerry O\'Connell, Corey Feldman, Kiefer Sutherland', 89, 7.9),
	(238, 'The Godfather', 'https://vidsrc.to/embed/movie/238', 'Spanning the years 1945 to 1955, a chronicle of the fictional Italian-American Corleone crime family. When organized crime family patriarch, Vito Corleone barely survives an attempt on his life, his youngest son, Michael steps in to take care of the would-be killers, launching a campaign of bloody revenge.', 'Drama, Crime', 'https://image.tmdb.org/t/p/w400/3bhkrj58Vtu7enYsRolD1fZdja1.jpg', '1972-03-14', 'Francis Ford Coppola', 'Marlon Brando, Al Pacino, James Caan, Robert Duvall, Richard S. Castellano', 175, 8.7),
	(239, 'Some Like It Hot', 'https://vidsrc.to/embed/movie/239', 'Two musicians witness a mob hit and struggle to find a way out of the city before they are found by the gangsters. Their only opportunity is to join an all-girl band as they leave on a tour. To make their getaway they must first disguise themselves as women, then keep their identities secret and deal with the problems this brings - such as an attractive bandmate and a very determined suitor.', 'Comedy, Romance, Crime', 'https://image.tmdb.org/t/p/w400/hVIKyTK13AvOGv7ICmJjK44DTzp.jpg', '1959-03-19', 'Billy Wilder', 'Tony Curtis, Jack Lemmon, Marilyn Monroe, George Raft, Pat O’Brien', 122, 8.1),
	(242, 'The Godfather Part III', 'https://vidsrc.to/embed/movie/242', 'In the midst of trying to legitimize his business dealings in 1979 New York and Italy, aging mafia don, Michael Corleone seeks forgiveness for his sins while taking a young protege under his wing.', 'Crime, Drama, Thriller', 'https://image.tmdb.org/t/p/w400/lm3pQ2QoQ16pextRsmnUbG2onES.jpg', '1990-12-25', 'Francis Ford Coppola', 'Al Pacino, Diane Keaton, Talia Shire, Andy García, Eli Wallach', 162, 7.4),
	(272, 'Batman Begins', 'https://vidsrc.to/embed/movie/272', 'Driven by tragedy, billionaire Bruce Wayne dedicates his life to uncovering and defeating the corruption that plagues his home, Gotham City.  Unable to work within the system, he instead creates a new identity, a symbol of fear for the criminal underworld - The Batman.', 'Action, Crime, Drama', 'https://image.tmdb.org/t/p/w400/4MpN4kIEqUjW8OPtOQJXlTdHiJV.jpg', '2005-06-10', 'Christopher Nolan', 'Christian Bale, Liam Neeson, Katie Holmes, Michael Caine, Gary Oldman', 140, 7.7),
	(274, 'The Silence of the Lambs', 'https://vidsrc.to/embed/movie/274', 'Clarice Starling is a top student at the FBI\'s training academy.  Jack Crawford wants Clarice to interview Dr. Hannibal Lecter, a brilliant psychiatrist who is also a violent psychopath, serving life behind bars for various acts of murder and cannibalism.  Crawford believes that Lecter may have insight into a case and that Starling, as an attractive young woman, may be just the bait to draw him out.', 'Crime, Drama, Thriller', 'https://image.tmdb.org/t/p/w400/uS9m8OBk1A8eM9I042bx8XXpqAq.jpg', '1991-02-14', 'Jonathan Demme', 'Jodie Foster, Anthony Hopkins, Scott Glenn, Ted Levine, Anthony Heald', 119, 8.3),
	(275, 'Fargo', 'https://vidsrc.to/embed/movie/275', 'Jerry, a small-town Minnesota car salesman is bursting at the seams with debt... but he\'s got a plan. He\'s going to hire two thugs to kidnap his wife in a scheme to collect a hefty ransom from his wealthy father-in-law. It\'s going to be a snap and nobody\'s going to get hurt... until people start dying. Enter Police Chief Marge, a coffee-drinking, parka-wearing - and extremely pregnant - investigator who\'ll stop at nothing to get her man. And if you think her small-time investigative skills will give the crooks a run for their ransom... you betcha!', 'Crime, Drama, Thriller', 'https://image.tmdb.org/t/p/w400/rt7cpEr1uP6RTZykBFhBTcRaKvG.jpg', '1996-03-08', 'Joel Coen', 'William H. Macy, Frances McDormand, Peter Stormare, Steve Buscemi, John Carroll Lynch', 98, 7.9),
	(279, 'Amadeus', 'https://vidsrc.to/embed/movie/279', 'Wolfgang Amadeus Mozart is a remarkably talented young Viennese composer who unwittingly finds a fierce rival in the disciplined and determined Antonio Salieri. Resenting Mozart for both his hedonistic lifestyle and his undeniable talent, the highly religious Salieri is gradually consumed by his jealousy and becomes obsessed with Mozart\'s downfall, leading to a devious scheme that has dire consequences for both men.', 'History, Music, Drama', 'https://image.tmdb.org/t/p/w400/1n5VUlCqgmVax1adxGZm8oCFaKc.jpg', '1984-09-19', 'Miloš Forman', 'F. Murray Abraham, Tom Hulce, Elizabeth Berridge, Roy Dotrice, Simon Callow', 161, 8.0),
	(280, 'Terminator 2: Judgment Day', 'https://vidsrc.to/embed/movie/280', 'Set ten years after the events of the original, James Cameron’s classic sci-fi action flick tells the story of a second attempt to get the rid of rebellion leader John Connor, this time targeting the boy himself. However, the rebellion has sent a reprogrammed terminator to protect Connor.', 'Action, Thriller, Science Fiction', 'https://image.tmdb.org/t/p/w400/5M0j0B18abtBI5gi2RhfjjurTqb.jpg', '1991-07-03', 'James Cameron', 'Arnold Schwarzenegger, Linda Hamilton, Edward Furlong, Robert Patrick, Joe Morton', 137, 8.1),
	(284, 'The Apartment', 'https://vidsrc.to/embed/movie/284', 'Bud Baxter is a minor clerk in a huge New York insurance company, until he discovers a quick way to climb the corporate ladder. He lends out his apartment to the executives as a place to take their mistresses. Although he often has to deal with the aftermath of their visits, one night he\'s left with a major problem to solve.', 'Comedy, Drama, Romance', 'https://image.tmdb.org/t/p/w400/hhSRt1KKfRT0yEhEtRW3qp31JFU.jpg', '1960-06-21', 'Billy Wilder', 'Jack Lemmon, Shirley MacLaine, Fred MacMurray, Ray Walston, Jack Kruschen', 125, 8.2),
	(289, 'Casablanca', 'https://vidsrc.to/embed/movie/289', 'In Casablanca, Morocco in December 1941, a cynical American expatriate meets a former lover, with unforeseen complications.', 'Drama, Romance', 'https://image.tmdb.org/t/p/w400/5K7cOHoay2mZusSLezBOY0Qxh8a.jpg', '1943-01-15', 'Michael Curtiz', 'Humphrey Bogart, Ingrid Bergman, Paul Henreid, Claude Rains, Conrad Veidt', 102, 8.2),
	(311, 'Once Upon a Time in America', 'https://vidsrc.to/embed/movie/311', 'A former Prohibition-era Jewish gangster returns to the Lower East Side of Manhattan over thirty years later, where he once again must confront the ghosts and regrets of his old life.', 'Drama, Crime', 'https://image.tmdb.org/t/p/w400/i0enkzsL5dPeneWnjl1fCWm6L7k.jpg', '1984-05-23', 'Sergio Leone', 'Robert De Niro, James Woods, Elizabeth McGovern, Treat Williams, Tuesday Weld', 229, 8.4),
	(329, 'Jurassic Park', 'https://vidsrc.to/embed/movie/329', 'A wealthy entrepreneur secretly creates a theme park featuring living dinosaurs drawn from prehistoric DNA. Before opening day, he invites a team of experts and his two eager grandchildren to experience the park and help calm anxious investors. However, the park is anything but amusing as the security systems go off-line and the dinosaurs escape.', 'Adventure, Science Fiction', 'https://image.tmdb.org/t/p/w400/oU7Oq2kFAAlGqbU4VoAE36g4hoI.jpg', '1993-06-11', 'Steven Spielberg', 'Sam Neill, Laura Dern, Jeff Goldblum, Richard Attenborough, Bob Peck', 127, 7.9),
	(335, 'Once Upon a Time in the West', 'https://vidsrc.to/embed/movie/335', 'As the railroad builders advance unstoppably through the Arizona desert on their way to the sea, Jill arrives in the small town of Flagstone with the intention of starting a new life.', 'Drama, Western', 'https://image.tmdb.org/t/p/w400/qbYgqOczabWNn2XKwgMtVrntD6P.jpg', '1968-12-21', 'Sergio Leone', 'Claudia Cardinale, Henry Fonda, Jason Robards, Charles Bronson, Gabriele Ferzetti', 166, 8.3),
	(346, 'Seven Samurai', 'https://vidsrc.to/embed/movie/346', 'A samurai answers a village\'s request for protection after he falls on hard times. The town needs protection from bandits, so the samurai gathers six others to help him teach the people how to defend themselves, and the villagers provide the soldiers with food.', 'Action, Drama', 'https://image.tmdb.org/t/p/w400/ApdijpVm1GNV9BQMOsGcAXq4gEB.jpg', '1954-04-26', 'Akira Kurosawa', 'Toshirō Mifune, Takashi Shimura, Yoshio Inaba, Seiji Miyaguchi, Minoru Chiaki', 207, 8.5),
	(348, 'Alien', 'https://vidsrc.to/embed/movie/348', 'During its return to the earth, commercial spaceship Nostromo intercepts a distress signal from a distant planet. When a three-member team of the crew discovers a chamber containing thousands of eggs on the planet, a creature inside one of the eggs attacks an explorer. The entire crew is unaware of the impending nightmare set to descend upon them when the alien parasite planted inside its unfortunate host is birthed.', 'Horror, Science Fiction', 'https://image.tmdb.org/t/p/w400/vfrQk5IPloGg1v9Rzbh2Eg3VGyM.jpg', '1979-05-25', 'Ridley Scott', 'Sigourney Weaver, Tom Skerritt, Ian Holm, Yaphet Kotto, John Hurt', 117, 8.2),
	(389, '12 Angry Men', 'https://vidsrc.to/embed/movie/389', 'The defense and the prosecution have rested and the jury is filing into the jury room to decide if a young Spanish-American is guilty or innocent of murdering his father. What begins as an open and shut case soon becomes a mini-drama of each of the jurors\' prejudices and preconceptions about the trial, the accused, and each other.', 'Drama', 'https://image.tmdb.org/t/p/w400/ow3wq89wM8qd5X7hWKxiRfsFf9C.jpg', '1957-04-10', 'Sidney Lumet', 'Martin Balsam, John Fiedler, Lee J. Cobb, E.G. Marshall, Jack Klugman', 97, 8.5),
	(393, 'Kill Bill: Vol. 2', 'https://vidsrc.to/embed/movie/393', 'The Bride unwaveringly continues on her roaring rampage of revenge against the band of assassins who had tried to kill her and her unborn child. She visits each of her former associates one-by-one, checking off the victims on her Death List Five until there\'s nothing left to do … but kill Bill.', 'Action, Crime, Thriller', 'https://image.tmdb.org/t/p/w400/2yhg0mZQMhDyvUQ4rG1IZ4oIA8L.jpg', '2004-04-16', 'Quentin Tarantino', 'Uma Thurman, David Carradine, Daryl Hannah, Michael Madsen, Gordon Liu Chia-hui', 136, 7.9),
	(406, 'La Haine', 'https://vidsrc.to/embed/movie/406', 'After a chaotic night of rioting in a marginal suburb of Paris, three young friends, Vinz, Hubert and Saïd, wander around unoccupied waiting for news about the state of health of a mutual friend who has been seriously injured when confronting the police.', 'Drama', 'https://image.tmdb.org/t/p/w400/8rgPyWjYZhsphSSxbXguMnhN7H0.jpg', '1995-05-31', 'Mathieu Kassovitz', 'Vincent Cassel, Hubert Koundé, Saïd Taghmaoui, Abdel Ahmed Ghili, Solo', 98, 8.1),
	(423, 'The Pianist', 'https://vidsrc.to/embed/movie/423', 'The true story of pianist Władysław Szpilman\'s experiences in Warsaw during the Nazi occupation. When the Jews of the city find themselves forced into a ghetto, Szpilman finds work playing in a café; and when his family is deported in 1942, he stays behind, works for a while as a laborer, and eventually goes into hiding in the ruins of the war-torn city.', 'Drama, War', 'https://image.tmdb.org/t/p/w400/2hFvxCCWrTmCYwfy7yum0GKRi3Y.jpg', '2002-09-17', 'Roman Polanski', 'Adrien Brody, Thomas Kretschmann, Frank Finlay, Maureen Lipman, Emilia Fox', 150, 8.4),
	(424, 'Schindler\'s List', 'https://vidsrc.to/embed/movie/424', 'The true story of how businessman Oskar Schindler saved over a thousand Jewish lives from the Nazis while they worked as slaves in his factory during World War II.', 'Drama, History, War', 'https://image.tmdb.org/t/p/w400/sF1U4EUQS8YHUYjNl3pMGNIQyr0.jpg', '1993-12-15', 'Steven Spielberg', 'Liam Neeson, Ben Kingsley, Ralph Fiennes, Caroline Goodall, Jonathan Sagall', 195, 8.6),
	(426, 'Vertigo', 'https://vidsrc.to/embed/movie/426', 'A retired San Francisco detective suffering from acrophobia investigates the strange activities of an old friend\'s wife, all the while becoming dangerously obsessed with her.', 'Mystery, Romance, Thriller', 'https://image.tmdb.org/t/p/w400/15uOEfqBNTVtDUT7hGBVCka0rZz.jpg', '1958-05-28', 'Alfred Hitchcock', 'James Stewart, Kim Novak, Barbara Bel Geddes, Tom Helmore, Henry Jones', 128, 8.2),
	(433, 'Mary Poppins', 'https://vidsrc.to/embed/movie/433', 'Mr Banks is looking for a nanny for his two mischievous children and comes across Mary Poppins, an angelic nanny. She not only brings a change in their lives but also spreads happiness.', 'Comedy, Family, Fantasy', 'https://image.tmdb.org/t/p/w400/1DjJh0vwGwi6KugPZfDiliO25XP.jpg', '1964-12-17', 'Robert Stevenson', 'Julie Andrews, Dick Van Dyke, David Tomlinson, Glynis Johns, Hermione Baddeley', 139, 7.6),
	(453, 'A Beautiful Mind', 'https://vidsrc.to/embed/movie/453', 'John Nash is a brilliant but asocial mathematician fighting schizophrenia. After he accepts secret work in cryptography, his life takes a turn for the nightmarish.', 'Drama, Romance', 'https://image.tmdb.org/t/p/w400/zwzWCmH72OSC9NA0ipoqw5Zjya8.jpg', '2001-12-14', 'Ron Howard', 'Russell Crowe, Jennifer Connelly, Ed Harris, Paul Bettany, Christopher Plummer', 135, 7.9),
	(489, 'Good Will Hunting', 'https://vidsrc.to/embed/movie/489', 'Will Hunting has a genius-level IQ but chooses to work as a janitor at MIT. When he solves a difficult graduate-level math problem, his talents are discovered by Professor Gerald Lambeau, who decides to help the misguided youth reach his potential. When Will is arrested for attacking a police officer, Professor Lambeau makes a deal to get leniency for him if he will get treatment from therapist Sean Maguire.', 'Drama', 'https://image.tmdb.org/t/p/w400/bABCBKYBK7A5G1x0FzoeoNfuj2.jpg', '1997-12-05', 'Gus Van Sant', 'Matt Damon, Robin Williams, Ben Affleck, Stellan Skarsgård, Minnie Driver', 127, 8.1),
	(490, 'The Seventh Seal', 'https://vidsrc.to/embed/movie/490', 'When disillusioned Swedish knight Antonius Block returns home from the Crusades to find his country in the grips of the Black Death, he challenges Death to a chess match for his life. Tormented by the belief that God does not exist, Block sets off on a journey, meeting up with traveling players Jof and his wife, Mia, and becoming determined to evade Death long enough to commit one redemptive act while he still lives.', 'Fantasy, Drama', 'https://image.tmdb.org/t/p/w400/wcZ21zrOsy0b52AfAF50XpTiv75.jpg', '1957-02-16', 'Ingmar Bergman', 'Max von Sydow, Bengt Ekerot, Gunnar Björnstrand, Nils Poppe, Bibi Andersson', 96, 8.2),
	(497, 'The Green Mile', 'https://vidsrc.to/embed/movie/497', 'A supernatural tale set on death row in a Southern prison, where gentle giant John Coffey possesses the mysterious power to heal people\'s ailments. When the cell block\'s head guard, Paul Edgecomb, recognizes Coffey\'s miraculous gift, he tries desperately to help stave off the condemned man\'s execution.', 'Fantasy, Drama, Crime', 'https://image.tmdb.org/t/p/w400/8VG8fDNiy50H4FedGwdSVUPoaJe.jpg', '1999-12-10', 'Frank Darabont', 'Tom Hanks, Michael Clarke Duncan, David Morse, Bonnie Hunt, Michael Jeter', 189, 8.5),
	(500, 'Reservoir Dogs', 'https://vidsrc.to/embed/movie/500', 'A botched robbery indicates a police informant, and the pressure mounts in the aftermath at a warehouse. Crime begets violence as the survivors -- veteran Mr. White, newcomer Mr. Orange, psychopathic parolee Mr. Blonde, bickering weasel Mr. Pink and Nice Guy Eddie -- unravel.', 'Crime, Thriller', 'https://image.tmdb.org/t/p/w400/xi8Iu6qyTfyZVDVy60raIOYJJmk.jpg', '1992-09-02', 'Quentin Tarantino', 'Harvey Keitel, Tim Roth, Michael Madsen, Chris Penn, Steve Buscemi', 99, 8.1),
	(510, 'One Flew Over the Cuckoo\'s Nest', 'https://vidsrc.to/embed/movie/510', 'A petty criminal fakes insanity to serve his sentence in a mental ward rather than prison. He soon finds himself as a leader to the other patients—and an enemy to the cruel, domineering nurse who runs the ward.', 'Drama', 'https://image.tmdb.org/t/p/w400/3jcbDmRFiQ83drXNOvRDeKHxS0C.jpg', '1975-11-19', 'Miloš Forman', 'Jack Nicholson, Brad Dourif, Louise Fletcher, Danny DeVito, William Redfield', 133, 8.4),
	(521, 'Dial M for Murder', 'https://vidsrc.to/embed/movie/521', 'An ex-tennis pro carries out a plot to have his wife murdered after discovering she is having an affair, and assumes she will soon leave him for the other man anyway. When things go wrong, he improvises a new plan—to frame her for murder instead.', 'Thriller, Crime', 'https://image.tmdb.org/t/p/w400/2gDCAgl2iBQNuJuk6p2xtuS1ewg.jpg', '1954-05-29', 'Alfred Hitchcock', 'Ray Milland, Grace Kelly, Robert Cummings, John Williams, Anthony Dawson', 105, 8.0),
	(524, 'Casino', 'https://vidsrc.to/embed/movie/524', 'In early-1970s Las Vegas, Sam "Ace" Rothstein gets tapped by his bosses to head the Tangiers Casino. At first, he\'s a great success in the job, but over the years, problems with his loose-cannon enforcer Nicky Santoro, his ex-hustler wife Ginger, her con-artist ex Lester Diamond and a handful of corrupt politicians put Sam in ever-increasing danger.', 'Crime, Drama, History', 'https://image.tmdb.org/t/p/w400/4TS5O1IP42bY2BvgMxL156EENy.jpg', '1995-11-22', 'Martin Scorsese', 'Robert De Niro, Sharon Stone, Joe Pesci, James Woods, Don Rickles', 179, 8.0),
	(548, 'Rashomon', 'https://vidsrc.to/embed/movie/548', 'Brimming with action while incisively examining the nature of truth, "Rashomon" is perhaps the finest film ever to investigate the philosophy of justice. Through an ingenious use of camera and flashbacks, Kurosawa reveals the complexities of human nature as four people recount different versions of the story of a man\'s murder and the rape of his wife.', 'Crime, Drama, Mystery', 'https://image.tmdb.org/t/p/w400/vL7Xw04nFMHwnvXRFCmYYAzMUvY.jpg', '1950-08-26', 'Akira Kurosawa', 'Toshirō Mifune, Machiko Kyō, Takashi Shimura, Masayuki Mori, Minoru Chiaki', 88, 8.1),
	(562, 'Die Hard', 'https://vidsrc.to/embed/movie/562', 'NYPD cop John McClane\'s plan to reconcile with his estranged wife is thrown for a serious loop when, minutes after he arrives at her office, the entire building is overtaken by a group of terrorists. With little help from the LAPD, wisecracking McClane sets out to single-handedly rescue the hostages and bring the bad guys down.', 'Action, Thriller', 'https://image.tmdb.org/t/p/w400/yFihWxQcmqcaBR31QM6Y8gT6aYV.jpg', '1988-07-15', 'John McTiernan', 'Bruce Willis, Alan Rickman, Alexander Godunov, Bonnie Bedelia, Reginald VelJohnson', 132, 7.8),
	(567, 'Rear Window', 'https://vidsrc.to/embed/movie/567', 'A wheelchair-bound photographer spies on his neighbors from his apartment window and becomes convinced one of them has committed murder.', 'Thriller, Mystery', 'https://image.tmdb.org/t/p/w400/ILVF0eJxHMddjxeQhswFtpMtqx.jpg', '1954-08-01', 'Alfred Hitchcock', 'James Stewart, Grace Kelly, Thelma Ritter, Wendell Corey, Raymond Burr', 112, 8.4),
	(578, 'Jaws', 'https://vidsrc.to/embed/movie/578', 'When an insatiable great white shark terrorizes the townspeople of Amity Island, the police chief, an oceanographer and a grizzled shark hunter seek to destroy the blood-thirsty beast.', 'Horror, Thriller, Adventure', 'https://image.tmdb.org/t/p/w400/lxM6kqilAdpdhqUl2biYp5frUxE.jpg', '1975-06-20', 'Steven Spielberg', 'Roy Scheider, Robert Shaw, Richard Dreyfuss, Murray Hamilton, Lorraine Gary', 124, 7.7),
	(579, 'Jaws 2', 'https://vidsrc.to/embed/movie/579', 'Police chief Brody must protect the citizens of Amity after a second monstrous shark begins terrorizing the waters.', 'Horror, Thriller', 'https://image.tmdb.org/t/p/w400/cN3ijEwsn4kBaRuHfcJpAQJbeWe.jpg', '1978-06-16', 'Jeannot Szwarc', 'Roy Scheider, Lorraine Gary, Murray Hamilton, Joseph Mascolo, Jeffrey Kramer', 116, 6.0),
	(580, 'Jaws: The Revenge', 'https://vidsrc.to/embed/movie/580', 'After another deadly shark attack, Ellen Brody decides she has had enough of New England\'s Amity Island and moves to the Caribbean to join her son, Michael, and his family. But a great white shark has followed her there, hungry for more lives.', 'Adventure, Thriller, Horror', 'https://image.tmdb.org/t/p/w400/kGiaOztahZV2x7bil7sbk7fb6ob.jpg', '1987-07-17', 'Joseph Sargent', 'Lorraine Gary, Lance Guest, Mario Van Peebles, Michael Caine, Karen Young', 89, 4.1),
	(581, 'Dances with Wolves', 'https://vidsrc.to/embed/movie/581', 'Wounded Civil War soldier, John Dunbar tries to commit suicide—and becomes a hero instead. As a reward, he\'s assigned to his dream post, a remote junction on the Western frontier, and soon makes unlikely friends with the local Sioux tribe.', 'Adventure, Drama, Western', 'https://image.tmdb.org/t/p/w400/hw0ZEHAaTqTxSXGVwUFX7uvanSA.jpg', '1990-03-30', 'Kevin Costner', 'Kevin Costner, Mary McDonnell, Graham Greene, Rodney A. Grant, Floyd \'Red Crow\' Westerman', 181, 7.8),
	(583, 'Life of Brian', 'https://vidsrc.to/embed/movie/583', 'Brian Cohen is an average young Jewish man, but through a series of ridiculous events, he gains a reputation as the Messiah. When he\'s not dodging his followers or being scolded by his shrill mother, the hapless Brian has to contend with the pompous Pontius Pilate and acronym-obsessed members of a separatist movement. Rife with Monty Python\'s signature absurdity, the tale finds Brian\'s life paralleling Biblical lore, albeit with many more laughs.', 'Comedy', 'https://image.tmdb.org/t/p/w400/lSSA64WF0M0BXnjwr2quMh6shCl.jpg', '1979-08-17', 'Terry Jones', 'Graham Chapman, John Cleese, Terry Gilliam, Eric Idle, Terry Jones', 94, 7.8),
	(595, 'To Kill a Mockingbird', 'https://vidsrc.to/embed/movie/595', 'Scout Finch, 6, and her older brother Jem live in sleepy Maycomb, Alabama, spending much of their time with their friend Dill and spying on their reclusive and mysterious neighbor, Boo Radley. When Atticus, their widowed father and a respected lawyer, defends a black man named Tom Robinson against fabricated rape charges, the trial and tangent events expose the children to evils of racism and stereotyping.', 'Drama', 'https://image.tmdb.org/t/p/w400/gZycFUMLx2110dzK3nBNai7gfpM.jpg', '1962-12-20', 'Robert Mulligan', 'Gregory Peck, Mary Badham, Phillip Alford, John Megna, Frank Overton', 129, 8.0),
	(596, 'The Grapes of Wrath', 'https://vidsrc.to/embed/movie/596', 'Tom Joad returns to his home after a jail sentence to find his family kicked out of their farm due to foreclosure. He catches up with them on his Uncle’s farm, and joins them the next day as they head for California and a new life... Hopefully.', 'Drama', 'https://image.tmdb.org/t/p/w400/eUcxMVBIA0Jg8l1RGUqycrc3eIQ.jpg', '1940-03-15', 'John Ford', 'Henry Fonda, Jane Darwell, John Carradine, Charley Grapewin, Dorris Bowdon', 129, 7.8),
	(598, 'City of God', 'https://vidsrc.to/embed/movie/598', 'In the slums of Rio, two kids\' paths diverge as one struggles to become a photographer and the other a kingpin.', 'Drama, Crime', 'https://image.tmdb.org/t/p/w400/k7eYdWvhYQyRQoU2TB2A2Xu2TfD.jpg', '2002-08-30', 'Fernando Meirelles', 'Alexandre Rodrigues, Leandro Firmino, Phellipe Haagensen, Douglas Silva, Jonathan Haagensen', 130, 8.4),
	(599, 'Sunset Boulevard', 'https://vidsrc.to/embed/movie/599', 'A hack screenwriter writes a screenplay for a former silent film star who has faded into Hollywood obscurity.', 'Drama', 'https://image.tmdb.org/t/p/w400/sC4Dpmn87oz9AuxZ15Lmip0Ftgr.jpg', '1950-08-17', 'Billy Wilder', 'William Holden, Gloria Swanson, Erich von Stroheim, Nancy Olson, Fred Clark', 110, 8.3),
	(600, 'Full Metal Jacket', 'https://vidsrc.to/embed/movie/600', 'A pragmatic U.S. Marine observes the dehumanizing effects the U.S.-Vietnam War has on his fellow recruits from their brutal boot camp training to the bloody street fighting in Hue.', 'Drama, War', 'https://image.tmdb.org/t/p/w400/kMKyx1k8hWWscYFnPbnxxN4Eqo4.jpg', '1987-06-26', 'Stanley Kubrick', 'Matthew Modine, Adam Baldwin, Vincent D\'Onofrio, R. Lee Ermey, Dorian Harewood', 117, 8.1),
	(614, 'Wild Strawberries', 'https://vidsrc.to/embed/movie/614', 'Crotchety retired doctor Isak Borg travels from Stockholm to Lund, Sweden, with his pregnant and unhappy daughter-in-law, Marianne, in order to receive an honorary degree from his alma mater. Along the way, they encounter a series of hitchhikers, each of whom causes the elderly doctor to muse upon the pleasures and failures of his own life. These include the vivacious young Sara, a dead ringer for the doctor\'s own first love.', 'Drama', 'https://image.tmdb.org/t/p/w400/iyTD2QnySNMPUPE3IedZQipSWfz.jpg', '1957-12-26', 'Ingmar Bergman', 'Victor Sjöström, Bibi Andersson, Ingrid Thulin, Gunnar Björnstrand, Jullan Kindahl', 91, 8.1),
	(619, 'The Bodyguard', 'https://vidsrc.to/embed/movie/619', 'A former Secret Service agent grudgingly takes an assignment to protect a pop idol who\'s threatened by a crazed fan. At first, the safety-obsessed bodyguard and the self-indulgent diva totally clash. But before long, all that tension sparks fireworks of another sort, and the love-averse tough guy is torn between duty and romance.', 'Thriller, Action, Drama, Music, Romance', 'https://image.tmdb.org/t/p/w400/9XfwOFcJ3Bz1gh3aD6xaeM58B0e.jpg', '1992-11-25', 'Mick Jackson', 'Whitney Houston, Kevin Costner, Gary Kemp, Bill Cobbs, Ralph Waite', 129, 6.7),
	(627, 'Trainspotting', 'https://vidsrc.to/embed/movie/627', 'Heroin addict Mark Renton stumbles through bad ideas and sobriety attempts with his unreliable friends -- Sick Boy, Begbie, Spud and Tommy. He also has an underage girlfriend, Diane, along for the ride. After cleaning up and moving from Edinburgh to London, Mark finds he can\'t escape the life he left behind when Begbie shows up at his front door on the lam, and a scheming Sick Boy follows.', 'Drama, Crime', 'https://image.tmdb.org/t/p/w400/bhY62Dw8iW54DIhxPQerbuB9DOP.jpg', '1996-02-23', 'Danny Boyle', 'Ewan McGregor, Ewen Bremner, Jonny Lee Miller, Kevin McKidd, Robert Carlyle', 94, 8.0),
	(629, 'The Usual Suspects', 'https://vidsrc.to/embed/movie/629', 'Held in an L.A. interrogation room, Verbal Kint attempts to convince the feds that a mythic crime lord, Keyser Soze, not only exists, but was also responsible for drawing him and his four partners into a multi-million dollar heist that ended with an explosion in San Pedro harbor – leaving few survivors. Verbal lures his interrogators with an incredible story of the crime lord\'s almost supernatural prowess.', 'Drama, Crime, Thriller', 'https://image.tmdb.org/t/p/w400/bUPmtQzrRhzqYySeiMpv7GurAfm.jpg', '1995-07-19', 'Bryan Singer', 'Gabriel Byrne, Kevin Spacey, Stephen Baldwin, Kevin Pollak, Benicio del Toro', 106, 8.2),
	(630, 'The Wizard of Oz', 'https://vidsrc.to/embed/movie/630', 'Young Dorothy finds herself in a magical world where she makes friends with a lion, a scarecrow and a tin man as they make their way along the yellow brick road to talk with the Wizard and ask for the things they miss most in their lives. The Wicked Witch of the West is the only thing that could stop them.', 'Adventure, Fantasy, Family', 'https://image.tmdb.org/t/p/w400/pfAZFD7I2hxW9HCChTuAzsdE6UX.jpg', '1939-08-15', 'Victor Fleming', 'Judy Garland, Ray Bolger, Jack Haley, Bert Lahr, Frank Morgan', 102, 7.6),
	(640, 'Catch Me If You Can', 'https://vidsrc.to/embed/movie/640', 'A true story about Frank Abagnale Jr. who, before his 19th birthday, successfully conned millions of dollars worth of checks as a Pan Am pilot, doctor, and legal prosecutor. An FBI agent makes it his mission to put him behind bars. But Frank not only eludes capture, he revels in the pursuit.', 'Drama, Crime', 'https://image.tmdb.org/t/p/w400/ctjEj2xM32OvBXCq8zAdK3ZrsAj.jpg', '2002-12-16', 'Steven Spielberg', 'Leonardo DiCaprio, Tom Hanks, Christopher Walken, Martin Sheen, Nathalie Baye', 141, 8.0),
	(641, 'Requiem for a Dream', 'https://vidsrc.to/embed/movie/641', 'The hopes and dreams of four ambitious people are shattered when their drug addictions begin spiraling out of control. A look into addiction and how it overcomes the mind and body.', 'Crime, Drama', 'https://image.tmdb.org/t/p/w400/nOd6vjEmzCT0k4VYqsA2hwyi87C.jpg', '2000-10-06', 'Darren Aronofsky', 'Ellen Burstyn, Jared Leto, Jennifer Connelly, Marlon Wayans, Christopher McDonald', 102, 8.0),
	(654, 'On the Waterfront', 'https://vidsrc.to/embed/movie/654', 'A dim-witted yet kind-hearted boxer, Terry Malloy, who failed to succeed unintentionally lures a man to his death after being tricked by a criminal called Johnny Friendly whose men pick of every man who has the courage to speak up to their crimes. As he works on the waterfronts that Friendly owns, he is sent to a church meeting run by a good preacher about how to deal with the problem and runs into the dead man’s sister. Slowly, he falls in love with her and begins to feel guilt about his crime.', 'Crime, Drama, Romance', 'https://image.tmdb.org/t/p/w400/fKjLZy9W8VxMOp5OoyWojmLVCQw.jpg', '1954-06-22', 'Elia Kazan', 'Marlon Brando, Karl Malden, Lee J. Cobb, Rod Steiger, Pat Henning', 108, 7.9),
	(665, 'Ben-Hur', 'https://vidsrc.to/embed/movie/665', 'In 25 AD, Judah Ben-Hur, a Jew in ancient Judea, opposes the occupying Roman empire.  Falsely accused by a Roman childhood friend-turned-overlord of trying to kill the Roman governor, he is put into slavery and his mother and sister are taken away as prisoners.', 'History, Drama, Adventure, Action', 'https://image.tmdb.org/t/p/w400/m4WQ1dBIrEIHZNCoAjdpxwSKWyH.jpg', '1959-11-18', 'William Wyler', 'Charlton Heston, Jack Hawkins, Haya Harareet, Stephen Boyd, Martha Scott', 222, 7.9),
	(679, 'Aliens', 'https://vidsrc.to/embed/movie/679', 'When Ripley\'s lifepod is found by a salvage crew over 50 years later, she finds that terra-formers are on the very planet they found the alien species. When the company sends a family of colonists out to investigate her story—all contact is lost with the planet and colonists. They enlist Ripley and the colonial marines to return and search for answers.', 'Action, Thriller, Science Fiction', 'https://image.tmdb.org/t/p/w400/r1x5JGpyqZU8PYhbs4UcrO1Xb6x.jpg', '1986-07-18', 'James Cameron', 'Sigourney Weaver, Michael Biehn, Lance Henriksen, Bill Paxton, Jenette Goldstein', 137, 7.9),
	(680, 'Pulp Fiction', 'https://vidsrc.to/embed/movie/680', 'A burger-loving hit man, his philosophical partner, a drug-addled gangster\'s moll and a washed-up boxer converge in this sprawling, comedic crime caper. Their adventures unfurl in three stories that ingeniously trip back and forth in time.', 'Thriller, Crime', 'https://image.tmdb.org/t/p/w400/d5iIlFn5s0ImszYzBPb8JPIfbXD.jpg', '1994-09-10', 'Quentin Tarantino', 'John Travolta, Samuel L. Jackson, Uma Thurman, Bruce Willis, Ving Rhames', 154, 8.5),
	(694, 'The Shining', 'https://vidsrc.to/embed/movie/694', 'Jack Torrance accepts a caretaker job at the Overlook Hotel, where he, along with his wife Wendy and their son Danny, must live isolated from the rest of the world for the winter. But they aren\'t prepared for the madness that lurks within.', 'Horror, Thriller', 'https://image.tmdb.org/t/p/w400/xazWoLealQwEgqZ89MLZklLZD3k.jpg', '1980-05-23', 'Stanley Kubrick', 'Jack Nicholson, Shelley Duvall, Danny Lloyd, Scatman Crothers, Philip Stone', 144, 8.2),
	(745, 'The Sixth Sense', 'https://vidsrc.to/embed/movie/745', 'Following an unexpected tragedy, child psychologist Malcolm Crowe meets a nine year old boy named Cole Sear, who is hiding a dark secret.', 'Mystery, Thriller, Drama', 'https://image.tmdb.org/t/p/w400/4AfSDjjCy6T5LA1TMz0Lh2HlpRh.jpg', '1999-08-06', 'M. Night Shyamalan', 'Bruce Willis, Haley Joel Osment, Toni Collette, Olivia Williams, Trevor Morgan', 107, 7.9),
	(752, 'V for Vendetta', 'https://vidsrc.to/embed/movie/752', 'In a world in which Great Britain has become a fascist state, a masked vigilante known only as “V” conducts guerrilla warfare against the oppressive British government. When V rescues a young woman from the secret police, he finds in her an ally with whom he can continue his fight to free the people of Britain.', 'Action, Thriller, Science Fiction', 'https://image.tmdb.org/t/p/w400/1avD1JeaRiJX5M4ahPdZPypGoGN.jpg', '2006-02-23', 'James McTeigue', 'Natalie Portman, Hugo Weaving, Stephen Rea, Stephen Fry, John Hurt', 132, 7.9),
	(762, 'Monty Python and the Holy Grail', 'https://vidsrc.to/embed/movie/762', 'King Arthur, accompanied by his squire, recruits his Knights of the Round Table, including Sir Bedevere the Wise, Sir Lancelot the Brave, Sir Robin the Not-Quite-So-Brave-As-Sir-Lancelot and Sir Galahad the Pure. On the way, Arthur battles the Black Knight who, despite having had all his limbs chopped off, insists he can still fight. They reach Camelot, but Arthur decides not  to enter, as "it is a silly place".', 'Adventure, Comedy, Fantasy', 'https://image.tmdb.org/t/p/w400/hWx1ANiWEWWyzKPN0us35HCGnhQ.jpg', '1975-05-25', 'Terry Gilliam', 'Graham Chapman, John Cleese, Eric Idle, Terry Gilliam, Terry Jones', 91, 7.8),
	(769, 'GoodFellas', 'https://vidsrc.to/embed/movie/769', 'The true story of Henry Hill, a half-Irish, half-Sicilian Brooklyn kid who is adopted by neighbourhood gangsters at an early age and climbs the ranks of a Mafia family under the guidance of Jimmy Conway.', 'Drama, Crime', 'https://image.tmdb.org/t/p/w400/aKuFiU82s5ISJpGZp7YkIr3kCUd.jpg', '1990-09-12', 'Martin Scorsese', 'Ray Liotta, Robert De Niro, Joe Pesci, Lorraine Bracco, Paul Sorvino', 145, 8.5),
	(770, 'Gone with the Wind', 'https://vidsrc.to/embed/movie/770', 'The spoiled daughter of a Georgia plantation owner conducts a tumultuous romance with a cynical profiteer during the American Civil War and Reconstruction Era.', 'Drama, War, Romance', 'https://image.tmdb.org/t/p/w400/lNz2Ow0wGCAvzckW7EOjE03KcYv.jpg', '1939-12-15', 'Victor Fleming', 'Vivien Leigh, Clark Gable, Olivia de Havilland, Leslie Howard, Hattie McDaniel', 233, 8.0),
	(780, 'The Passion of Joan of Arc', 'https://vidsrc.to/embed/movie/780', 'A classic of the silent age, this film tells the story of the doomed but ultimately canonized 15th-century teenage warrior. On trial for claiming she\'d spoken to God, Jeanne d\'Arc is subjected to inhumane treatment and scare tactics at the hands of church court officials. Initially bullied into changing her story, Jeanne eventually opts for what she sees as the truth. Her punishment, a famously brutal execution, earns her perpetual martyrdom.', 'Drama, History', 'https://image.tmdb.org/t/p/w400/8OYGtQlO8k9PcOm49apV62eVJQo.jpg', '1928-04-21', 'Carl Theodor Dreyer', 'Maria Falconetti, Eugène Silvain, André Berley, Maurice Schutz, Antonin Artaud', 82, 8.0),
	(792, 'Platoon', 'https://vidsrc.to/embed/movie/792', 'As a young and naive recruit in Vietnam, Chris Taylor faces a moral crisis when confronted with the horrors of war and the duality of man.', 'Drama, War, Action', 'https://image.tmdb.org/t/p/w400/m3mmFkPQKvPZq5exmh0bDuXlD9T.jpg', '1986-12-19', 'Oliver Stone', 'Tom Berenger, Willem Dafoe, Charlie Sheen, Forest Whitaker, Francesco Quinn', 120, 7.7),
	(797, 'Persona', 'https://vidsrc.to/embed/movie/797', 'A young nurse, Alma, is put in charge of Elisabeth Vogler: an actress who is seemingly healthy in all respects, but will not talk. As they spend time together, Alma speaks to Elisabeth constantly, never receiving any answer. The time they spend together only strengthens the crushing realization that one does not exist.', 'Drama', 'https://image.tmdb.org/t/p/w400/bdXbfUir63TQHA5NTNcXPICZCW8.jpg', '1966-10-18', 'Ingmar Bergman', 'Bibi Andersson, Liv Ullmann, Margaretha Krook, Gunnar Björnstrand, Jörgen Lindström', 83, 8.2),
	(812, 'Aladdin', 'https://vidsrc.to/embed/movie/812', 'Princess Jasmine grows tired of being forced to remain in the palace, so she sneaks out into the marketplace, in disguise, where she meets street urchin Aladdin. The couple falls in love, although Jasmine may only marry a prince. After being thrown in jail, Aladdin becomes embroiled in a plot to find a mysterious lamp, with which the evil Jafar hopes to rule the land.', 'Animation, Family, Adventure, Fantasy, Romance', 'https://image.tmdb.org/t/p/w400/oakAd8syy7jNQ4ZoaAGCQkTqcOV.jpg', '1992-11-25', 'Ron Clements', 'Scott Weinger, Robin Williams, Linda Larkin, Jonathan Freeman, Gilbert Gottfried', 95, 7.6),
	(821, 'Judgment at Nuremberg', 'https://vidsrc.to/embed/movie/821', 'In 1947, four German judges who served on the bench during the Nazi regime face a military tribunal to answer charges of crimes against humanity. Chief Justice Haywood hears evidence and testimony not only from lead defendant Ernst Janning and his defense attorney Hans Rolfe, but also from the widow of a Nazi general, an idealistic U.S. Army captain and reluctant witness Irene Wallner.', 'Drama', 'https://image.tmdb.org/t/p/w400/cqumfgd74ftVzOKJpFGOG64WlXL.jpg', '1961-12-18', 'Stanley Kramer', 'Spencer Tracy, Burt Lancaster, Richard Widmark, Marlene Dietrich, Maximilian Schell', 190, 8.0),
	(826, 'The Bridge on the River Kwai', 'https://vidsrc.to/embed/movie/826', 'The classic story of English POWs in Burma forced to build a bridge to aid the war effort of their Japanese captors. British and American intelligence officers conspire to blow up the structure, but Col. Nicholson , the commander who supervised the bridge\'s construction, has acquired a sense of pride in his creation and tries to foil their plans.', 'Drama, History, War', 'https://image.tmdb.org/t/p/w400/7paXMt2e3Tr5dLmEZOGgFEn2Vo7.jpg', '1957-10-11', 'David Lean', 'William Holden, Alec Guinness, Jack Hawkins, Sessue Hayakawa, James Donald', 161, 7.8),
	(829, 'Chinatown', 'https://vidsrc.to/embed/movie/829', 'Private eye Jake Gittes lives off of the murky moral climate of sunbaked, pre-World War II Southern California. Hired by a beautiful socialite to investigate her husband\'s extra-marital affair, Gittes is swept into a maelstrom of double dealings and deadly deceits, uncovering a web of personal and political scandals that come crashing together.', 'Crime, Drama, Mystery, Thriller', 'https://image.tmdb.org/t/p/w400/mQJz8J4naOA7RmCtypTPjgXZIMY.jpg', '1974-06-20', 'Roman Polanski', 'Jack Nicholson, Faye Dunaway, John Huston, Perry Lopez, John Hillerman', 130, 7.9),
	(832, 'M', 'https://vidsrc.to/embed/movie/832', 'In this classic German thriller, Hans Beckert, a serial killer who preys on children, becomes the focus of a massive Berlin police manhunt. Beckert\'s heinous crimes are so repellant and disruptive to city life that he is even targeted by others in the seedy underworld network. With both cops and criminals in pursuit, the murderer soon realizes that people are on his trail, sending him into a tense, panicked attempt to escape justice.', 'Drama, Thriller, Crime', 'https://image.tmdb.org/t/p/w400/slsS6jT6SXjcTPOrsFcLIrHboXA.jpg', '1931-05-11', 'Fritz Lang', 'Peter Lorre, Ellen Widmann, Inge Landgut, Otto Wernicke, Theodor Loos', 111, 8.1),
	(857, 'Saving Private Ryan', 'https://vidsrc.to/embed/movie/857', 'As U.S. troops storm the beaches of Normandy, three brothers lie dead on the battlefield, with a fourth trapped behind enemy lines. Ranger captain John Miller and seven men are tasked with penetrating German-held territory and bringing the boy home.', 'Drama, History, War', 'https://image.tmdb.org/t/p/w400/uqx37cS8cpHg8U35f9U5IBlrCV3.jpg', '1998-07-24', 'Steven Spielberg', 'Tom Hanks, Tom Sizemore, Edward Burns, Matt Damon, Barry Pepper', 169, 8.2),
	(862, 'Toy Story', 'https://vidsrc.to/embed/movie/862', 'Led by Woody, Andy\'s toys live happily in his room until Andy\'s birthday brings Buzz Lightyear onto the scene. Afraid of losing his place in Andy\'s heart, Woody plots against Buzz. But when circumstances separate Buzz and Woody from their owner, the duo eventually learns to put aside their differences.', 'Animation, Adventure, Family, Comedy', 'https://image.tmdb.org/t/p/w400/uXDfjJbdP4ijW5hWSBrPrlKpxab.jpg', '1995-10-30', 'John Lasseter', 'Tom Hanks, Tim Allen, Don Rickles, Jim Varney, Wallace Shawn', 81, 8.0),
	(863, 'Toy Story 2', 'https://vidsrc.to/embed/movie/863', 'Andy heads off to Cowboy Camp, leaving his toys to their own devices. Things shift into high gear when an obsessive toy collector named Al McWhiggen, owner of Al\'s Toy Barn kidnaps Woody. Andy\'s toys mount a daring rescue mission, Buzz Lightyear meets his match and Woody has to decide where he and his heart truly belong.', 'Animation, Comedy, Family', 'https://image.tmdb.org/t/p/w400/2MFIhZAW0CVlEQrFyqwa4U6zqJP.jpg', '1999-10-30', 'John Lasseter', 'Tom Hanks, Tim Allen, Joan Cusack, Kelsey Grammer, Don Rickles', 92, 7.6),
	(872, 'Singin\' in the Rain', 'https://vidsrc.to/embed/movie/872', 'In 1927 Hollywood, a silent film production company and cast make a difficult transition to sound.', 'Comedy, Romance', 'https://image.tmdb.org/t/p/w400/w03EiJVHP8Un77boQeE7hg9DVdU.jpg', '1952-04-09', 'Stanley Donen', 'Gene Kelly, Donald O\'Connor, Debbie Reynolds, Jean Hagen, Millard Mitchell', 103, 8.2),
	(877, 'Scarface', 'https://vidsrc.to/embed/movie/877', 'In 1920s Chicago, Italian immigrant and notorious thug, Antonio "Tony" Camonte, shoots his way to the top of the mobs while trying to protect his sister from the criminal life.', 'Crime, Drama', 'https://image.tmdb.org/t/p/w400/y4E5oRiHMTFkEB12IIcpbKbKzDW.jpg', '1932-04-09', 'Howard Hawks', 'Paul Muni, Ann Dvorak, Karen Morley, Osgood Perkins, C. Henry Gordon', 93, 7.5),
	(887, 'The Best Years of Our Lives', 'https://vidsrc.to/embed/movie/887', 'It\'s the hope that sustains the spirit of every GI: the dream of the day when he will finally return home. For three WWII veterans, the day has arrived. But for each man, the dream is about to become a nightmare.', 'Drama, Romance, War', 'https://image.tmdb.org/t/p/w400/gd5EoAU4MM57sW3vlWxJ0NMM8cV.jpg', '1946-12-25', 'William Wyler', 'Fredric March, Myrna Loy, Dana Andrews, Teresa Wright, Virginia Mayo', 171, 7.8),
	(901, 'City Lights', 'https://vidsrc.to/embed/movie/901', 'In this sound-era silent film, a tramp falls in love with a beautiful blind flower seller.', 'Comedy, Romance, Drama', 'https://image.tmdb.org/t/p/w400/bXNvzjULc9jrOVhGfjcc64uKZmZ.jpg', '1931-02-01', 'Charlie Chaplin', 'Charlie Chaplin, Virginia Cherrill, Harry Myers, Al Ernest Garcia, Hank Mann', 87, 8.3),
	(903, 'Cool Hand Luke', 'https://vidsrc.to/embed/movie/903', 'When petty criminal Luke Jackson is sentenced to two years in a Florida prison farm, he doesn\'t play by the rules of either the sadistic warden or the yard\'s resident heavy, Dragline, who ends up admiring the new guy\'s unbreakable will. Luke\'s bravado, even in the face of repeated stints in the prison\'s dreaded solitary confinement cell, "the box," make him a rebel hero to his fellow convicts and a thorn in the side of the prison officers.', 'Crime, Drama', 'https://image.tmdb.org/t/p/w400/4ykzTiHKLamh3eZJ8orVICtU2Jp.jpg', '1967-11-01', 'Stuart Rosenberg', 'Paul Newman, George Kennedy, Luke Askew, Morgan Woodward, Harry Dean Stanton', 127, 7.7),
	(935, 'Dr. Strangelove or: How I Learned to Stop Worrying and Love the Bomb', 'https://vidsrc.to/embed/movie/935', 'After the insane General Jack D. Ripper initiates a nuclear strike on the Soviet Union, a war room full of politicians, generals and a Russian diplomat all frantically try to stop the nuclear strike.', 'Comedy, War', 'https://image.tmdb.org/t/p/w400/7SixLzxcqezkZEYU8pcHZgbkmjp.jpg', '1964-01-29', 'Stanley Kubrick', 'Peter Sellers, George C. Scott, Sterling Hayden, Keenan Wynn, Slim Pickens', 95, 8.1),
	(938, 'For a Few Dollars More', 'https://vidsrc.to/embed/movie/938', 'Two bounty hunters are in pursuit of "El Indio," one of the most wanted fugitives in the western territories, and his gang.', 'Western', 'https://image.tmdb.org/t/p/w400/ooqASvA7qxlTVKL3KwOzBwy57Dh.jpg', '1965-12-18', 'Sergio Leone', 'Clint Eastwood, Lee Van Cleef, Gian Maria Volonté, Luigi Pistilli, Klaus Kinski', 132, 8.0),
	(947, 'Lawrence of Arabia', 'https://vidsrc.to/embed/movie/947', 'The story of British officer T.E. Lawrence\'s mission to aid the Arab tribes in their revolt against the Ottoman Empire during the First World War. Lawrence becomes a flamboyant, messianic figure in the cause of Arab unity but his psychological instability threatens to undermine his achievements.', 'Adventure, Drama, History, War', 'https://image.tmdb.org/t/p/w400/AiAm0EtDvyGqNpVoieRw4u65vD1.jpg', '1962-12-11', 'David Lean', 'Peter O\'Toole, Alec Guinness, Anthony Quinn, Jack Hawkins, Omar Sharif', 228, 8.0),
	(949, 'Heat', 'https://vidsrc.to/embed/movie/949', 'Obsessive master thief Neil McCauley leads a top-notch crew on various daring heists throughout Los Angeles while determined detective Vincent Hanna pursues him without rest. Each man recognizes and respects the ability and the dedication of the other even though they are aware their cat-and-mouse game may end in violence.', 'Action, Crime, Drama', 'https://image.tmdb.org/t/p/w400/umSVjVdbVwtx5ryCA2QXL44Durm.jpg', '1995-12-15', 'Michael Mann', 'Al Pacino, Robert De Niro, Val Kilmer, Jon Voight, Tom Sizemore', 170, 7.9),
	(961, 'The General', 'https://vidsrc.to/embed/movie/961', 'During America’s Civil War, Union spies steal engineer Johnny Gray\'s beloved locomotive, \'The General\'—with Johnnie\'s lady love aboard an attached boxcar—and he single-handedly must do all in his power to both get The General back and to rescue Annabelle.', 'Comedy, War, Adventure, Action, History, Romance', 'https://image.tmdb.org/t/p/w400/4NmV1Wei4LxT2lpjViCAScgCZLq.jpg', '1926-12-25', 'Buster Keaton', 'Buster Keaton, Marion Mack, Glen Cavender, Jim Farley, Frederick Vroom', 79, 8.0),
